import React, { useEffect, useState } from "react";
import "./style.css";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Typewriter from "typewriter-effect";
import { introdata, meta } from "../../content_option";
import { Link } from "react-router-dom";

export const Home = () => {
  const [code, setCode] = useState("");
  const [showImage, setShowImage] = useState(false);

  useEffect(() => {
    const initialCode = `function performEnigmaticRitual() {
  let arcaneChant = "Get image ";
  let mysticalIngredient = "eye of newt";

  for (let i = 0; i < 13; i++) {
    arcaneChant += i * 3;
  }

  mysticalIngredient = mysticalIngredient.split('').reverse().join('');

  let forbiddenGlyphs = [];

  for (let i = 0; i < 7; i++) {
    forbiddenGlyphs.push(i * 5);
  }

  let eldritchCodex = {
    ancientIncantation: arcaneChant,
    darkIngredient: mysticalIngredient,
    crypticSymbols: forbiddenGlyphs,
  };

  for (let key in eldritchCodex) {
    if (eldritchCodex.hasOwnProperty(key)) {
      console.log(eldritchCodex[key]);
    }
  }

  let mysteriousInvocation = function() {
    let mysticalResult = arcaneChant + mysticalIngredient;
    return mysticalResult;
  };

  return mysteriousInvocation();
}

console.log(performEnigmaticRitual());
connecting...
success!
`;

    let index = 0;

    const addNextCharacter = () => {
      setCode((prevCode) => prevCode + initialCode.charAt(index));
      index++;

      if (index < initialCode.length) {
        setTimeout(addNextCharacter, 12); // Adjust the typing speed
      } else {
        // После набора кода, показываем изображение
        setTimeout(() => {
          setShowImage(true);
        }, 1000); // Задержка перед показом изображения
      }
    };

    addNextCharacter();
  }, []);

  return (
      <HelmetProvider>
        <section id="home" className="home">
          <Helmet>
            <meta charSet="utf-8" />
            <title>{meta.title}</title>
            <meta name="description" content={meta.description} />
          </Helmet>
          <div className="intro_sec d-block d-lg-flex align-items-center ">
            <div className="h_bg-image order-1 order-lg-2 h-100">
              {showImage ? (
                  <img
                      src={introdata.your_img_url}
                      alt="Your Image"
                      style={{ width: "100%", height: "100%" }}
                  />
              ) : (
                  <pre>{code}</pre>
              )}
            </div>
            <div className="text order-2 order-lg-1 h-200 d-lg-flex justify-content-center">
              <div className="align-self-center ">
                <div className="intro mx-auto">
                  <h2 className="mb-1x">{introdata.title}</h2>
                  <h1 className="fluidz-48 mb-1x">
                    <Typewriter
                        options={{
                          strings: [
                            introdata.animated.first,
                            introdata.animated.second,
                            introdata.animated.third,
                          ],
                          autoStart: true,
                          loop: true,
                          deleteSpeed: 10,
                        }}
                    />
                  </h1>
                  <div className="intro_btn-action pb-5">
                    <Link to="/about" className="text_2">
                      <div id="button_p" className="ac_btn btn ">
                        Обо Мне
                        <div className="ring one"></div>
                        <div className="ring two"></div>
                        <div className="ring three"></div>
                      </div>
                    </Link>

                    <Link to="/portfolio">
                      <div id="button_h" className="ac_btn btn">
                        Портфолио
                        <div className="ring one"></div>
                        <div className="ring two"></div>
                        <div className="ring three"></div>
                      </div>
                    </Link>
                    <Link to="/contact">
                      <div id="button_h" className="ac_btn btn">
                        Контакты
                        <div className="ring one"></div>
                        <div className="ring two"></div>
                        <div className="ring three"></div>
                      </div>
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </HelmetProvider>
  );
};
