import React, { useEffect, useState } from "react";
import "./style.css";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Typewriter from "typewriter-effect";
import { introdata, meta } from "../../content_option";
import { Link } from "react-router-dom";

export const Home = () => {
  useEffect(() => {
    const matrixText = document.getElementById("matrix-text");
    const characters = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()-=_+[]{}|;:'\",.<>/?`~";

    const getRandomCharacter = () => characters[Math.floor(Math.random() * characters.length)];

    const generateMatrixCode = () => {
      let matrixCode = "";
      const codeLength = 100; // Adjust the length as needed

      for (let i = 0; i < codeLength; i++) {
        matrixCode += getRandomCharacter();
      }

      return matrixCode;
    };

    const updateMatrixText = () => {
      matrixText.textContent = generateMatrixCode();
    };

    const matrixInterval = setInterval(updateMatrixText, 100); // Adjust the interval as needed

    return () => clearInterval(matrixInterval);
  }, []);

  return (
      <HelmetProvider>
        <section id="home" className="home">
          <Helmet>
            <meta charSet="utf-8" />
            <title>{meta.title}</title>
            <meta name="description" content={meta.description} />
          </Helmet>
          <div className="intro_sec d-block d-lg-flex align-items-center ">
            <div className="text order-2 order-lg-1 h-200 d-lg-flex justify-content-center">
              <div className="align-self-center ">
                <div className="intro mx-auto">
                  <h2 className="mb-1x">{introdata.title}</h2>
                  <h1 className="fluidz-48 mb-1x">
                    <span id="matrix-text"></span>
                  </h1>
                  <div className="intro_btn-action pb-5">
                    <Link to="/about" className="text_2">
                      <div id="button_p" className="ac_btn btn ">
                        Обо Мне
                        <div className="ring one"></div>
                        <div className="ring two"></div>
                        <div className="ring three"></div>
                      </div>
                    </Link>

                    <Link to="/portfolio">
                      <div id="button_h" className="ac_btn btn">
                        Портфолио
                        <div className="ring one"></div>
                        <div className="ring two"></div>
                        <div className="ring three"></div>
                      </div>
                    </Link>
                    <Link to="/contact">
                      <div id="button_h" className="ac_btn btn">
                        Контакты
                        <div className="ring one"></div>
                        <div className="ring two"></div>
                        <div className="ring three"></div>
                      </div>
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </HelmetProvider>
  );
};
